mapper.select:
select user_id,title,content,a.id,user_account,description,visit_number,praise_number,comment_number,collection_number,
       tags,cover_image,status,permissions,review_opinions,a.create_time,a.update_time
from tb_user as u , tb_article as a
where a.user_id = u.id

   and  user_account like CONCAT('%',#{userAccount},'%')


   and (title like  CONCAT('%',#{content},'%') or content like CONCAT('%',#{content},'%')  or description like CONCAT('%',#{content},'%'))


    and  a.status = #{status}


    and permissions = #{permissions}

LIMIT ${(current - 1) * pageSize},${pageSize};

select a.id, a.user_id,title,username,u.avatar,a.description,visit_number,praise_number,comment_number,collection_number, tags,cover_image,a.update_time,permissions from tb_user as u , tb_article as a where a.user_id = u.id
and (a.title like CONCAT('%',#{content},'%') or a.content like CONCAT('%',#{content},'%') or a.description like CONCAT('%',#{content},'%'))
and a.status = 0 ;

select a.id, a.user_id,title,username,u.avatar,a.description,visit_number,praise_number,comment_number,collection_number, tags,cover_image,a.update_time from tb_user as u , tb_article as a where a.user_id = u.id
and (a.title like CONCAT('%',#{articleAllRequest.content},'%') or a.content like CONCAT('%',#{articleAllRequest.content},'%') or a.description like CONCAT('%',#{articleAllRequest.content},'%'))
and a.status = 0 and ( a.permissions = 0 or (a.permissions = 2 and a.user_id in
#{userId}
)) ;
